
public class Code02_07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2, result;
		num1 = 100;
		num2 = 50;
		
		result = num1 + num2;
		System.out.println(num1 + "+" + num2 + "=" + result);
		
		result = num1 - num2;
		System.out.println(num1 + "-" + num2 + "=" + result);
		
		result = num1 * num2;
		System.out.println(num1 + "*" + num2 + "=" + result);
		
		result = num1 / num2;
		System.out.println(num1 + "/" + num2 + "=" + result);
		
	}

}
