
public class Code03_09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 20;
		num++;
		System.out.print(num+" ");
		num--;
		System.out.print(num+" ");
		num += 3;
		System.out.print(num+" ");
		num -= 3;
		System.out.print(num+" ");
		num *= 3;
		System.out.print(num+" ");
		num /= 3;
		System.out.print(num+" ");
		num %= 3;
		System.out.print(num+" ");
		
	}

}
